import React, { useState } from 'react';
import { format } from 'date-fns';

const ThoughtDumper = () => {
  const [thought, setThought] = useState('');
  const [summary, setSummary] = useState('');

  const handleThoughtSubmit = async () => {
    const timestamp = format(new Date(), 'yyyy-MM-dd HH:mm:ss');
    const response = await fetch('/api/summarize', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        thought,
        timestamp
      }),
    });
    
    const data = await response.json();
    setSummary(data.summary);
  };

  return (
    <div className="thought-dumper">
      <div className="thought-input">
        <h2>What's on your mind?</h2>
        <p className="subtitle">(only one big idea please...)</p>
        <textarea
          value={thought}
          onChange={(e) => setThought(e.target.value)}
          placeholder="Share your thought here..."
        />
        <div className="timestamp">
          {format(new Date(), 'yyyy-MM-dd HH:mm:ss')}
        </div>
      </div>
      
      <div className="thought-summary">
        <button 
          className="summarize-btn"
          onClick={handleThoughtSubmit}
        >
          Summarize
        </button>
        <div className="summary-content">
          {summary}
        </div>
      </div>
    </div>
  );
};

export default ThoughtDumper;
